import{_ as t,c as d,G as r,b as a}from"./chunks/framework.BrC0DRXd.js";const e=JSON.parse('{"title":"IDEA 常用快捷键","description":"介绍 IDEA 的常用快捷键","frontmatter":{"title":"IDEA 常用快捷键","tags":["IDEA"],"categories":["技巧"],"date":"2023-09-21T00:00:00.000Z","description":"介绍 IDEA 的常用快捷键","articleGPT":"这是一篇介绍 IDEA 的常用快捷键的文章, 包括日常使用、System.out、查找、跳转切换、编码相关、代码阅读相关、版本管理相关。","head":[["link",{"rel":"canonical","href":"https://indulgeback.github.io/posts/Java/1、IDEA常用快捷键"}]]},"headers":[],"relativePath":"posts/Java/1、IDEA常用快捷键.md","filePath":"posts/Java/1、IDEA常用快捷键.md","lastUpdated":1740135853000}');const l=t({name:"posts/Java/1、IDEA常用快捷键.md"},[["render",function(t,e,l,h,i,o){return a(),d("div",null,e[0]||(e[0]=[r('<h3 id="日常使用" tabindex="-1">日常使用 <a class="header-anchor" href="#日常使用" aria-label="Permalink to &quot;日常使用&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键组合</th><th>实现效果</th></tr></thead><tbody><tr><td>psvm + Tab键 / main + Tab键</td><td>public static void main(String[] args)</td></tr><tr><td>sout + Tab键</td><td>System.out.println()</td></tr><tr><td>Ctrl + X</td><td>删除当前行</td></tr><tr><td>Ctrl +D</td><td>复制当前行</td></tr><tr><td>Alt + j</td><td>查找下一个相同单词</td></tr><tr><td>Ctrl + Shift + j</td><td>选中所有相同单词</td></tr><tr><td>Alt+Insert(或右键Generate)</td><td>生成代码(如get,set方法,构造函数等)</td></tr><tr><td>Ctrl+Alt+T</td><td>生成try catch （或者 Alt+enter选择）</td></tr><tr><td>CTRL+ALT+T</td><td>把选中的代码放在 TRY{} IF{} ELSE{} 里</td></tr><tr><td>Ctr+shift+U</td><td>实现大小写之间的转化</td></tr><tr><td>ALT+回车</td><td>导入包,自动修正</td></tr><tr><td>CTRL+ALT+L</td><td>格式化代码</td></tr><tr><td>CTRL+ALT+I</td><td>自动缩进</td></tr><tr><td>CTRL+E</td><td>最近更改的代码</td></tr><tr><td>fori</td><td>生成for (int i = 0; i &lt; ; i++) {}</td></tr><tr><td>Alt + &lt;–左右–&gt;键</td><td>实现窗口左右更换（多窗口）</td></tr><tr><td>Ctrl + 鼠标点击</td><td>快速找到成员变量的出处</td></tr><tr><td>Shift+F6</td><td>重构/重命名 (包、类、方法、变量、甚至注释等)</td></tr><tr><td>CTRL+Q</td><td>查看当前方法的声明</td></tr><tr><td>Ctrl+Alt+V</td><td>自动创建变量（new 对象();之后选择按快捷键）</td></tr><tr><td>Ctrl+O</td><td>重写方法</td></tr><tr><td>Ctrl+I</td><td>实现方法</td></tr><tr><td>ALT+/</td><td>代码提示</td></tr><tr><td>Ctrl+Shift+R</td><td>在当前项目中替换指定内容</td></tr><tr><td>Ctrl+E</td><td>最近编辑的文件列表</td></tr><tr><td>Ctrl+P</td><td>显示方法参数信息</td></tr><tr><td>Ctrl+Shift+Insert</td><td>查看历史复制记录，idea可以保留历史复制的 100 条记录</td></tr></tbody></table></div><h3 id="system-out" tabindex="-1">System.out <a class="header-anchor" href="#system-out" aria-label="Permalink to &quot;System.out&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>生成控制台的相关快捷键</th><th>描述</th></tr></thead><tbody><tr><td>sout + Tab键</td><td>生成System.out.println();，输出到控制台语句并换行。</td></tr><tr><td>souf + Tab键</td><td>生成System.out.printf(&quot;&quot;);,输出一个格式化字符串到控制台。</td></tr><tr><td>soutm + Tab键</td><td>生成System.out.println(&quot;类名.方法名&quot;);，输出当前 类和方法名 到控制台。</td></tr><tr><td>soutp + Tab键</td><td>生成System.out.println(所有方法参数名+值);，输出当前 方法的参数名和值 到控制台。</td></tr></tbody></table></div><h3 id="查找" tabindex="-1">查找 <a class="header-anchor" href="#查找" aria-label="Permalink to &quot;查找&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键</th><th>介绍</th></tr></thead><tbody><tr><td>Ctrl + F</td><td>在当前文件进行文本查找</td></tr><tr><td>Ctrl + R</td><td>在当前文件进行文本替换</td></tr><tr><td>Shift + Ctrl + F</td><td>在项目进行文本查找</td></tr><tr><td>Shift + Ctrl + R</td><td>在项目进行文本替换</td></tr><tr><td>Shift + Shift</td><td>快速搜索</td></tr><tr><td>Ctrl + N</td><td>查找class</td></tr><tr><td>Ctrl + Shift + N</td><td>查找文件</td></tr><tr><td>Ctrl + Shift + Alt + N</td><td>查找symbol（查找某个方法名）</td></tr></tbody></table></div><h3 id="跳转切换" tabindex="-1">跳转切换 <a class="header-anchor" href="#跳转切换" aria-label="Permalink to &quot;跳转切换&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键</th><th>介绍</th></tr></thead><tbody><tr><td>Ctrl + E</td><td>最近文件</td></tr><tr><td>Ctrl + Tab</td><td>切换文件</td></tr><tr><td>Ctrl + Alt + ←/→</td><td>跳转历史光标所在处</td></tr><tr><td>Alt + ←/→ 方向键</td><td>切换子tab</td></tr><tr><td>Ctrl + G</td><td>go to（跳转指定行号）</td></tr></tbody></table></div><h3 id="编码相关" tabindex="-1">编码相关 <a class="header-anchor" href="#编码相关" aria-label="Permalink to &quot;编码相关&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键</th><th>介绍</th></tr></thead><tbody><tr><td>Ctrl + W</td><td>快速选中</td></tr><tr><td>(Shift + Ctrl) + Alt + J</td><td>快速选中同文本</td></tr><tr><td>Ctrl + C/Ctrl + X/Ctrl + D</td><td>快速复制或剪切</td></tr><tr><td>多行选中 Tab / Shift + Tab</td><td>tab</td></tr><tr><td>Ctrl + Y</td><td>删除整行</td></tr><tr><td>滚轮点击变量/方法/类</td><td>快速进入变量/方法/类的定义处</td></tr><tr><td>Shift + 点击Tab</td><td>快速关闭tab</td></tr><tr><td>Ctrl + Z 、Ctrl + Shift + Z</td><td>后悔药，撤销/取消撤销</td></tr><tr><td>Ctrl + Shift + enter</td><td>自动收尾，代码自动补全</td></tr><tr><td>Alt + enter</td><td>IntelliJ IDEA 根据光标所在问题，提供快速修复选择，光标放在的位置不同提示的结果也不同</td></tr><tr><td>Alt + ↑/↓</td><td>方法快速跳转</td></tr><tr><td>F2</td><td>跳转到下一个高亮错误 或 警告位置</td></tr><tr><td>Alt + Insert</td><td>代码自动生成，如生成对象的 set / get 方法，构造函数，toString() 等</td></tr><tr><td>Ctrl + Shift + L</td><td>格式化代码</td></tr><tr><td>Shift + F6</td><td>快速修改方法名、变量名、文件名、类名等</td></tr><tr><td>Ctrl + F6</td><td>快速修改方法签名</td></tr></tbody></table></div><h3 id="代码阅读相关" tabindex="-1">代码阅读相关 <a class="header-anchor" href="#代码阅读相关" aria-label="Permalink to &quot;代码阅读相关&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键</th><th>介绍</th></tr></thead><tbody><tr><td>Ctrl + P</td><td>方法参数提示显示</td></tr><tr><td>Ctrl + Shift + i</td><td>就可以在当前类里再弹出一个窗口出来</td></tr><tr><td>Alt + F7</td><td>可以列出变量在哪些地方被使用了</td></tr><tr><td>光标在子类接口名，Ctrl + u</td><td>跳到父类接口</td></tr><tr><td>Alt + F1 + 1， esc</td><td></td></tr><tr><td>(Shift) + Ctrl + +/-</td><td>代码块折叠</td></tr><tr><td>Ctrl + Shift + ←/→</td><td>移动窗口分割线</td></tr><tr><td>Ctrl + (Alt) + B</td><td>跳转方法定义/实现</td></tr><tr><td>Ctrl + H</td><td>类的层级关系</td></tr><tr><td>Ctrl + F12</td><td>Show Members 类成员快速显示</td></tr></tbody></table></div><h3 id="版本管理相关" tabindex="-1">版本管理相关 <a class="header-anchor" href="#版本管理相关" aria-label="Permalink to &quot;版本管理相关&quot;">​</a></h3><div class="table-container"><table><thead><tr><th>快捷键</th><th>介绍</th></tr></thead><tbody><tr><td>Ctrl + D</td><td>Show Diff</td></tr><tr><td>(Shift) + F7</td><td>（上）下一处修改</td></tr></tbody></table></div>',14)]))}]]);export{e as __pageData,l as default};
